            children: "#include <bits/stdc++.h>\r\nusing namespace std;\r\n\r\nusing ll = long long;\r\nusing db = long double; // or double, if TL is tight\r\nusing str = string; // yay python!\r\n\r\nusing pi = pair<int,int>;\r\nusing pl = pair<ll,ll>;\r\nusing pd = pair<db,db>;\r\n\r\nusing vi = vector<int>;\r\nusing vb = vector<bool>;\r\nusing vl = vector<ll>;\r\nusing vd = vector<db>;\r\nusing vs = vector<str>;\r\nusing vpi = vector<pi>;\r\nusing vpl = vector<pl>;\r\nusing vpd = vector<pd>;\r\n\r\n#define tcT template<class T\r\n#define tcTU tcT, class U\r\n// ^ lol this makes everything look weird but I'll try it\r\ntcT> using V = vector<T>;\r\ntcT, size_t SZ> using AR = array<T,SZ>;\r\ntcT> using PR = pair<T,T>;\r\n\r\n// pairs\r\n#define mp make_pair\r\n#define f first\r\n#define s second\r\n\r\n// vectors\r\n// oops size(x), rbegin(x), rend(x) need C++17\r\n#define sz(x) int((x).size())\r\n#define bg(x) begin(x)\r\n#define all(x) bg(x), end(x)\r\n#define rall(x) x.rbegin(), x.rend()\r\n#define sor(x) sort(all(x))\r\n#define rsz resize\r\n#define ins insert\r\n#define ft front()\r\n#define bk back()\r\n#define pb push_back\r\n#define eb emplace_back\r\n#define pf push_front\r\n#define rtn return\r\n\r\n#define lb lower_bound\r\n#define ub upper_bound\r\ntcT> int lwb(V<T>& a, const T& b) { return int(lb(all(a),b)-bg(a)); }\r\n\r\n// loops\r\n#define FOR(i,a,b) for (int i = (a); i < (b); ++i)\r\n#define F0R(i,a) FOR(i,0,a)\r\n#define ROF(i,a,b) for (int i = (b)-1; i >= (a); --i)\r\n#define R0F(i,a) ROF(i,0,a)\r\n#define EACH(a,x) for (auto& a: x)\r\n\r\nconst int MOD = 1e9+7; // 998244353;\r\nconst int MX = 2e5+5;\r\nconst ll INF = 1e18; // not too close to LLONG_MAX\r\nconst db PI = acos((db)-1);\r\nconst int dx[4] = {1,0,-1,0}, dy[4] = {0,1,0,-1}; // for every grid problem!!\r\nmt19937 rng((uint32_t)chrono::steady_clock::now().time_since_epoch().count());\r\ntemplate<class T> using pqg = priority_queue<T,vector<T>,greater<T>>;\r\n\r\n// bitwise ops\r\n// also see https://gcc.gnu.org/onlinedocs/gcc/Other-Builtins.html\r\nconstexpr int pct(int x) { return __builtin_popcount(x); } // # of bits set\r\nconstexpr int bits(int x) { // assert(x >= 0); // make C++11 compatible until USACO updates ...\r\n\treturn x == 0 ? 0 : 31-__builtin_clz(x); } // floor(log2(x))\r\nconstexpr int p2(int x) { return 1<<x; }\r\nconstexpr int msk2(int x) { return p2(x)-1; }\r\n\r\nll cdiv(ll a, ll b) { return a/b+((a^b)>0&&a%b); } // divide a by b rounded up\r\nll fdiv(ll a, ll b) { return a/b-((a^b)<0&&a%b); } // divide a by b rounded down\r\n\r\ntcT> bool ckmin(T& a, const T& b) {\r\n\treturn b < a ? a = b, 1 : 0; } // set a = min(a,b)\r\ntcT> bool ckmax(T& a, const T& b) {\r\n\treturn a < b ? a = b, 1 : 0; }\r\n\r\ntcTU> T fstTrue(T lo, T hi, U f) {\r\n\thi ++; assert(lo <= hi); // assuming f is increasing\r\n\twhile (lo < hi) { // find first index such that f is true\r\n\t\tT mid = lo+(hi-lo)/2;\r\n\t\tf(mid) ? hi = mid : lo = mid+1;\r\n\t}\r\n\treturn lo;\r\n}\r\ntcTU> T lstTrue(T lo, T hi, U f) {\r\n\tlo --; assert(lo <= hi); // assuming f is decreasing\r\n\twhile (lo < hi) { // find first index such that f is true\r\n\t\tT mid = lo+(hi-lo+1)/2;\r\n\t\tf(mid) ? lo = mid : hi = mid-1;\r\n\t}\r\n\treturn lo;\r\n}\r\ntcT> void remDup(vector<T>& v) { // sort and remove duplicates\r\n\tsort(all(v)); v.erase(unique(all(v)),end(v)); }\r\ntcTU> void erase(T& t, const U& u) { // don't erase\r\n\tauto it = t.find(u); assert(it != end(t));\r\n\tt.erase(it); } // element that doesn't exist from (multi)set\r\n\r\n// INPUT\r\n#define tcTUU tcT, class ...U\r\ntcT> void re(complex<T>& c);\r\ntcTU> void re(pair<T,U>& p);\r\ntcT> void re(V<T>& v);\r\ntcT, size_t SZ> void re(AR<T,SZ>& a);\r\n\r\ntcT> void re(T& x) { cin >> x; }\r\nvoid re(double& d) { str t; re(t); d = stod(t); }\r\nvoid re(long double& d) { str t; re(t); d = stold(t); }\r\ntcTUU> void re(T& t, U&... u) { re(t); re(u...); }\r\n\r\ntcT> void re(complex<T>& c) { T a,b; re(a,b); c = {a,b}; }\r\ntcTU> void re(pair<T,U>& p) { re(p.f,p.s); }\r\ntcT> void re(V<T>& x) { EACH(a,x) re(a); }\r\ntcT, size_t SZ> void re(AR<T,SZ>& x) { EACH(a,x) re(a); }\r\ntcT> void rv(int n, V<T>& x) { x.rsz(n); re(x); }\r\n\r\n// TO_STRING\r\n#define ts to_string\r\nstr ts(char c) { return str(1,c); }\r\nstr ts(const char* s) { return (str)s; }\r\nstr ts(str s) { return s; }\r\nstr ts(bool b) {\r\n\t// #ifdef LOCAL\r\n\t// \treturn b ? \"true\" : \"false\";\r\n\t// #else\r\n\treturn ts((int)b);\r\n\t// #endif\r\n}\r\ntcT> str ts(complex<T> c) {\r\n\tstringstream ss; ss << c; return ss.str(); }\r\nstr ts(V<bool> v) {\r\n\tstr res = \"{\"; F0R(i,sz(v)) res += char('0'+v[i]);\r\n\tres += \"}\"; return res; }\r\ntemplate<size_t SZ> str ts(bitset<SZ> b) {\r\n\tstr res = \"\"; F0R(i,SZ) res += char('0'+b[i]);\r\n\treturn res; }\r\ntcTU> str ts(pair<T,U> p);\r\ntcT> str ts(T v) { // containers with begin(), end()\r\n\t#ifdef LOCAL\r\n\t\tbool fst = 1; str res = \"{\";\r\n\t\tfor (const auto& x: v) {\r\n\t\t\tif (!fst) res += \", \";\r\n\t\t\tfst = 0; res += ts(x);\r\n\t\t}\r\n\t\tres += \"}\"; return res;\r\n\t#else\r\n\t\tbool fst = 1; str res = \"\";\r\n\t\tfor (const auto& x: v) {\r\n\t\t\tif (!fst) res += \" \";\r\n\t\t\tfst = 0; res += ts(x);\r\n\t\t}\r\n\t\treturn res;\r\n\r\n\t#endif\r\n}\r\ntcTU> str ts(pair<T,U> p) {\r\n\t#ifdef LOCAL\r\n\t\treturn \"(\"+ts(p.f)+\", \"+ts(p.s)+\")\";\r\n\t#else\r\n\t\treturn ts(p.f)+\" \"+ts(p.s);\r\n\t#endif\r\n}\r\n\r\n// OUTPUT\r\ntcT> void pr(T x) { cout << ts(x); }\r\ntcTUU> void pr(const T& t, const U&... u) {\r\n\tpr(t); pr(u...); }\r\nvoid ps() { pr(\"\\n\"); } // print w/ spaces\r\ntcTUU> void ps(const T& t, const U&... u) {\r\n\tpr(t); if (sizeof...(u)) pr(\" \"); ps(u...); }\r\n\r\n// DEBUG\r\nvoid DBG() { cerr << \"]\" << endl; }\r\ntcTUU> void DBG(const T& t, const U&... u) {\r\n\tcerr << ts(t); if (sizeof...(u)) cerr << \", \";\r\n\tDBG(u...); }\r\n#ifdef LOCAL // compile with -DLOCAL, chk -> fake assert\r\n\t#define dbg(...) cerr << \"Line(\" << __LINE__ << \") -> [\" << #__VA_ARGS__ << \"]: [\", DBG(__VA_ARGS__)\r\n\t#define chk(...) if (!(__VA_ARGS__)) cerr << \"Line(\" << __LINE__ << \") -> function(\" \\\r\n\t\t << __FUNCTION__  << \") -> CHK FAILED: (\" << #__VA_ARGS__ << \")\" << \"\\n\", exit(0);\r\n#else\r\n\t#define dbg(...) 0\r\n\t#define chk(...) 0\r\n#endif\r\n\r\nvoid setPrec() { cout << fixed << setprecision(15); }\r\nvoid unsyncIO() { cin.tie(0)->sync_with_stdio(0); }\r\n// FILE I/O\r\nvoid setIn(str s) { freopen(s.c_str(),\"r\",stdin); }\r\nvoid setOut(str s) { freopen(s.c_str(),\"w\",stdout); }\r\nvoid setIO(str s = \"\") {\r\n\tunsyncIO(); setPrec();\r\n\t// cin.exceptions(cin.failbit);\r\n\t// throws exception when do smth illegal\r\n\t// ex. try to read letter into int\r\n\tif (sz(s)) setIn(s+\".in\"), setOut(s+\".out\"); // for USACO\r\n}\r\n\r\nint max_len;\r\nll mul = 1;\r\nll ans;\r\nint N,K1,K2;\r\nvi adj[MX];\r\n\r\nint get_prefix(const deque<int>& a, int mx) {\r\n\tif (mx < 0) return 0;\r\n\tif (mx+1 >= sz(a)) return a[0];\r\n\treturn a[0]-a[mx+1];\r\n}\r\n\r\nvoid comb(deque<int>& a, deque<int>& b) {\r\n\tif (sz(a) < sz(b)) swap(a,b);\r\n\tF0R(i,sz(b)-1) b[i] -= b[i+1];\r\n\tF0R(i,sz(b)) ans += (ll)b[i]*(get_prefix(a,K2-i)-get_prefix(a,K1-1-i));\r\n\tR0F(i,sz(b)-1) b[i] += b[i+1];\r\n\tF0R(i,sz(b)) a[i] += b[i];\r\n}\r\n\r\ndeque<int> dfs(int x, int p) { // each deque stores prefix sums\r\n\tdeque<int> res{1};\r\n\tEACH(y,adj[x]) if (y != p) {\r\n\t\tdeque<int> a = dfs(y,x);\r\n\t\ta.push_front(a.ft);\r\n\t\tcomb(res,a);\r\n\t}\r\n\treturn res;\r\n}\r\n\r\nint main() {\r\n\tsetIO(); re(N,K1,K2);\r\n\tF0R(i,N-1) {\r\n\t\tint a,b; re(a,b);\r\n\t\tadj[a].pb(b), adj[b].pb(a);\r\n\t}\r\n\tmax_len = K1-1, mul = -1;\r\n\tdfs(1,0);\r\n\tps(ans);\r\n\t// you should actually read the stuff at the bottom\r\n}\r\n\r\n/* stuff you should look for\r\n\t* int overflow, array bounds\r\n\t* special cases (n=1?)\r\n\t* do smth instead of nothing and stay organized\r\n\t* WRITE STU