_ << \"]: [\", DBG(__VA_ARGS__)\r\n\t#define chk(...) if (!(__VA_ARGS__)) cerr << \"Line(\" << __LINE__ << \") -> function(\" \\\r\n\t\t << __FUNCTION__  << \") -> CHK FAILED: (\" << #__VA_ARGS__ << \")\" << \"\\n\", exit(0);\r\n#else\r\n\t#define dbg(...) 0\r\n\t#define chk(...) 0\r\n#endif\r\n\r\nvoid setPrec() { cout << fixed << setprecision(15); }\r\nvoid unsyncIO() { cin.tie(0)->sync_with_stdio(0); }\r\n// FILE I/O\r\nvoid setIn(str s) { freopen(s.c_str(),\"r\",stdin); }\r\nvoid setOut(str s) { freopen(s.c_str(),\"w\",stdout); }\r\nvoid setIO(str s = \"\") {\r\n\tunsyncIO(); setPrec();\r\n\t// cin.exceptions(cin.failbit);\r\n\t// throws exception when do smth illegal\r\n\t// ex. try to read letter into int\r\n\tif (sz(s)) setIn(s+\".in\"), setOut(s+\".out\"); // for USACO\r\n}\r\n\r\narray<ld,10001> ans, tmp;\r\nbitset<10001> z[1300];\r\nbool pri[10001];\r\nvi co, CO;\r\n\r\nvoid fin(int x) {\r\n    pi cur = {x,sz(co)-1};\r\n    vi zz;\r\n    int lastUse = 0;\r\n    while (cur.s != -1) {\r\n        if (z[cur.s][cur.f] && lastUse != CO[cur.s]) {\r\n            lastUse = CO[cur.s];\r\n            cur.f -= co[cur.s]; zz.pb(co[cur.s]);\r\n        }\r\n        cur.s --;\r\n    }\r\n    sort(all(zz));\r\n    int sum = 0; trav(t,zz) sum += t;\r\n    vi ret;\r\n    sum = x-sum;\r\n    F0R(i,sum) ret.pb(i+1);\r\n    trav(t,zz) {\r\n        FOR(j,sum,sum+t-1) ret.pb(j+2);\r\n        ret.pb(sum+1);\r\n        sum += t;\r\n    }\r\n    trav(z,ret) pr(z,' ');\r\n    ps();\r\n}\r\n\r\nint main() {\r\n    setIO();\r\n    int d; re(d); vi v(d); re(v);\r\n    int mx = 0; trav(t,v) ckmax(mx,t);\r\n    FOR(i,2,mx+1) pri[i] = 1;\r\n    F0R(i,mx+1) ans[i] = 1;\r\n    FOR(i,2,mx+1) if (pri[i]) {\r\n        tmp = ans;\r\n        for (int j = 2*i; j <= mx; j += i) pri[j] = 0;\r\n        for (int j = 1; pow(i,j) <= mx; j ++) {\r\n            int x = pow(i,j);\r\n            FOR(k,x,mx+1) {\r\n                if (x*ans[k-x] > tmp[k]) {\r\n                    tmp[k] = x*ans[k-x];\r\n    