				}
				else if (xval[i] > *v && yval[i] < *h)
				{
					bottomright++;
				}
				else
				{
					topright++;
				}
			}
			maximum = max({bottomleft, topleft, bottomright, topright});
			answer = min(answer, maximum);
		}
	}
	cout << answer << endl;
	return 0;
}
