using namespace std;
using ll = long long;

bool cmp (const pair<int, int>& a, const pair<int, int>& b) {
	return a.first > b.first;
}
int main() {
	freopen("rental.in", "r", stdin);
	freopen("rental.out", "w", stdout);

	int n, m, r;
	cin >> n >> m >> r;

	vector<int> production(n);

	for (int i = 0; i < n; i++) {
		cin >> production[i];
	}

	vector<pair<int, int>> shops(m);

	for (int i = 0; i < m; i++) {
