import java.io.*;

public class OutOfSorts {
	// class made to hold the value and original index
	static class Entry implements Comparable<Entry> {
		public int value;
		public int index;

		public Entry (int v, int i) {
			value = v;
			index = i;
		}

		public int compareTo(Entry other) { 
