public class Loan {
	// our checking function in the binary search
	static boolean isOk (long numGallons, long withinDays, long atLeast, long checkNumber) {
		long g = 0;
		while (withinDays > 0 && g < numGallons) {
			long y = (numGallons - g) / checkNumber;
